INSERT INTO t_p90119217_django_layout_develo.reviews (name, text, rating, order_num, created_at) VALUES
('Мария Петрова', 'Казбек - потрясающий преподаватель! Я пришла очень застенчивой и боялась камеры. Теперь я уверенно выступаю перед публикой и даже снялась в своем первом короткометражном фильме. Спасибо за профессионализм!', 5, 1, NOW()),
('Андрей Соколов', 'Курс превзошел все мои ожидания! Раньше я боялся публичных выступлений, а теперь провожу презентации на работе с удовольствием. Методика Казбека действительно работает.', 5, 2, NOW()),
('Екатерина Волкова', 'Очень довольна курсом! Атмосфера на занятиях дружелюбная, преподаватель внимателен к каждому ученику. Научилась не только актерскому мастерству, но и обрела уверенность в себе.', 5, 3, NOW()),
('Дмитрий Новиков', 'Отличный курс для тех, кто хочет развить навыки публичных выступлений. Казбек дал конкретные инструменты и техники, которые я использую каждый день в работе.', 5, 4, NOW()),
('Анна Смирнова', 'Спасибо огромное за этот курс! Я нашла в себе силы преодолеть страх сцены и теперь участвую в театральных постановках. Казбек - мастер своего дела!', 5, 5, NOW()),
('Сергей Иванов', 'Прошел курс по рекомендации коллеги и не пожалел. Научился структурировать свои мысли, правильно использовать голос и жесты. Это изменило мою карьеру!', 5, 6, NOW());
